(649, 1, '2017-10-06 06:52:58', '2017-10-06 06:52:58', '<!DOCTYPE html>\r\n<html>\r\n<body>\r\n<style>\r\nbody {\r\n    background-color: #f8f8f8;\r\n    color: #222;\r\n    text-align: center;\r\n    font-family: "Segoe UI Light", "Sourse Sans Pro", sans-serif;\r\n}\r\n\r\na {\r\n    text-decoration: none;\r\n    color: #222;\r\n}\r\n\r\na:hover {\r\n    color: #333;\r\n}\r\n\r\n.canvas {\r\n    -webkit-box-shadow: 0 0 30px rgba(0, 0, 0, 0.3);\r\n    -moz-box-shadow: 0 0 30px rgba(0, 0, 0, 0.3);\r\n    box-shadow: 0 0 30px rgba(0, 0, 0, 0.3);\r\n    width: 400px;\r\n    height: 400px;\r\n    margin: auto;\r\n    background-color: #eee;\r\n    position: relative;\r\n    border: 2px solid #DDD;\r\n    box-sizing: border-box;\r\n}\r\n\r\n.canvas > div {\r\n    width: 100px;\r\n    height: 100px;\r\n    position: absolute;\r\n    line-height: 100px;\r\n    font-size: 2.4em;\r\n    background-color: #F1F1F1;\r\n    color: #4B5F6D;\r\n    font-weight: bold;\r\n    font-family: "Segoe UI Light", sans-serif;\r\n    box-sizing: border-box;\r\n    border: 1px solid #DDD;\r\n}\r\n\r\n.canvas > div:not(.null) {\r\n    transition: .1s ease left, .1s ease top;\r\n    z-index: 50;\r\n}\r\n\r\n.win {\r\n    position: fixed;\r\n    transform: translate(-50%, -50%);\r\n    left: 50%;\r\n    top: 50%;\r\n    z-index: 600;\r\n    border-radius: 15px;\r\n    background-color: #fff;\r\n    box-shadow: 0 0 10px 900px rgba(0, 0, 0, .3), 0 0 15px rgba(0, 0, 0, 0.5);\r\n    text-transform: uppercase;\r\n    padding: 30px;\r\n    display: none;\r\n}\r\n\r\n.win p {\r\n    font-size: 4em;\r\n}\r\n\r\n.btn {\r\n    display: inline-block;\r\n    border-radius: 3px;\r\n    background-color: #fff;\r\n    border: 1px solid #000;\r\n    padding: 5px 15px;\r\n    font-weight: bold;\r\n}\r\n\r\n.btn:hover {\r\n    background-color: #fafafa;\r\n}\r\n</style>\r\n\r\n<h2>Puzzle Game</h2>\r\n\r\n<h3>Made by <a href="https://github.com/mohebifar">Mohamad Mohebifar</a></h3>\r\n\r\n<p>Moves: <span id="moves">0</span> - Use arrow keys to move pieces</p>\r\n\r\n<div class="canvas"></div>\r\n\r\n<div class="win">\r\n    <p>You win !</p>\r\n    <a class="btn" href="#">New Game</a>\r\n</div>\r\n\r\n<p>\r\n    <a class="btn" href="#">New Game</a>\r\n</p>\r\n\r\n<script>\r\n/**\r\n * Puzzle Class\r\n *\r\n * @constructor\r\n */\r\nvar Puzzle = function Puzzle(el) {\r\n  this.matrix = [];\r\n  this.el = document.querySelector(el);\r\n  this.movesEl = document.getElementById(''moves'');\r\n  this._moves = 0;\r\n  this._lock = false;\r\n};\r\n\r\nPuzzle.getNeighbors = function (index) {\r\n  var result = {\r\n    top: null,\r\n    bottom: null,\r\n    left: null,\r\n    right: null\r\n  };\r\n\r\n  if ([3, 7, 11, 15].indexOf(index) === -1) {\r\n    result.right = index + 1;\r\n  }\r\n\r\n  if ([0, 4, 8, 12].indexOf(index) === -1) {\r\n    result.left = index - 1;\r\n  }\r\n\r\n  if (index > 3) {\r\n    result.top = index - 4;\r\n  }\r\n\r\n  if (index < 12) {\r\n    result.bottom = index + 4;\r\n  }\r\n\r\n  return result;\r\n};\r\n\r\nPuzzle.getPositionByIndex = function (index) {\r\n  return [Math.floor(index % 4) * 100, Math.floor(index / 4) * 100];\r\n};\r\n\r\nPuzzle.prototype.getNeighbors = function (index) {\r\n  var neighbors = Puzzle.getNeighbors(index);\r\n\r\n  for (var position in neighbors) {\r\n    var value = neighbors[position];\r\n    if (value !== null) {\r\n      neighbors[position] = this.matrix[value];\r\n    }\r\n  }\r\n\r\n  return neighbors;\r\n};\r\n\r\nPuzzle.prototype.shuffle = function () {\r\n  var matrix = this.matrix;\r\n  var tempArray = [];\r\n  var n = matrix.length;\r\n  for (var i = 0; i < n; i++) {\r\n    tempArray.push(matrix.splice(Math.floor(Math.random() * matrix.length), 1)[0]);\r\n  }\r\n\r\n  this.matrix = tempArray;\r\n\r\n  return this;\r\n};\r\n\r\nPuzzle.prototype.createNumbers = function () {\r\n  for (var i = 0; i < 16; i++) {\r\n    this.matrix[i] = new Piece(this);\r\n    this.matrix[i].el = document.createElement(''div'');\r\