ineSetter__(''moves'', function (moves) {\r\n  this._moves = moves;\r\n  this.movesEl.innerHTML = this._moves;\r\n});\r\n\r\nPuzzle.prototype.unlock = function () {\r\n  this._lock = false;\r\n};\r\n\r\nPuzzle.prototype.lock = function () {\r\n  this._lock = true;\r\n};\r\n\r\n/**\r\n * Pieces Class\r\n *\r\n * @param puzzle\r\n * @constructor\r\n */\r\nvar Piece = function Piece(puzzle) {\r\n  this.el = null;\r\n  this.parent = puzzle;\r\n  this.isNull = false;\r\n  this.realIndex = 0;\r\n};\r\n\r\nPiece.prototype.__defineGetter__(''index'', function () {\r\n  return this.parent.matrix.indexOf(this);\r\n});\r\n\r\nPiece.prototype.getNeighbors = function () {\r\n  return this.parent.getNeighbors(this.index);\r\n};\r\n\r\nPiece.prototype.replace = function (piece) {\r\n  if (piece) {\r\n    var myIndex = this.index,\r\n      thatIndex = piece.index;\r\n\r\n    this.parent.matrix[myIndex] = piece;\r\n    this.parent.matrix[thatIndex] = this;\r\n\r\n    return this;\r\n  }\r\n};\r\n\r\nvar puzzle = new Puzzle(''.canvas'').createNumbers().shuffle().updatePositions();\r\n\r\nwindow.addEventListener(''keydown'', function (e) {\r\n  var key = e.keyCode;\r\n\r\n  switch (key) {\r\n    case 37:\r\n      puzzle.move(''left'');\r\n      e.preventDefault();\r\n      break;\r\n    case 38:\r\n      puzzle.move(''up'');\r\n      e.preventDefault();\r\n      break;\r\n    case 39:\r\n      puzzle.move(''right'');\r\n      e.preventDefault();\r\n      break;\r\n    case 40:\r\n      puzzle.move(''down'');\r\n      e.preventDefault();\r\n      break;\r\n  }\r\n});\r\n\r\nfunction newGame() {\r\n  puzzle.moves = 0;\r\n  puzzle.shuffle().updatePositions().unlock();\r\n}\r\n\r\nvar buttons = document.querySelectorAll(''.btn'');\r\n\r\nfor (var i = 0; i < buttons.length; i++) {\r\n  buttons[i]\r\n    .addEventListener(''click'', function (e) {\r\n      e.preventDefault();\r\n\r\n      newGame();\r\n    });\r\n}\r\n\r\ndocument.querySelector(''.win'').addEventListener(''click'', function