.show();\r\n}\r\nfunction showInfo() {\r\n    $("#back").toggle();\r\n    $("#menu").hide();\r\n    $("#autor").show();\r\n}\r\nfunction exit(){\r\n    c=confirm("Are you sure?");\r\n    if (c==true){\r\n        window.location.href = "#";\r\n    }\r\n}\r\nfunction hide() {\r\n    $("#svg svg").hide();\r\n}\r\nfunction startpass(){\r\n    boxy = new Array();\r\n    hiden="";\r\n    hub=wordp;\r\n    i=hub.length;\r\n    for(i=0; i<hub.length; i++)\r\n    {\r\n        hiden=hiden+"*";\r\n        boxy[i]="*";\r\n    }\r\n    $("#pass").text(hiden);\r\n}\r\nfunction startgame(){\r\n    errors=0;\r\n    n=Math.floor(Math.random() * 110);\r\n    wordp=word[n];\r\n    clearAlphabet();\r\n}\r\nfunction jump(){\r\n    if(toggle==0){\r\n        $("#stickman").css("animation","jmp 4s ease-out ");\r\n        toggle=1;\r\n    }\r\n    else{\r\n        $("#stickman").css("animation","none");\r\n        toggle=0;\r\n    }\r\n}\r\n\r\nfunction szubienica(){\r\n    errors++;\r\n    if(errors<11){\r\n        $("#t"+errors).show();\r\n    }\r\n    else{\r\n        $("#t"+errors).show();\r\n        alert("You lost! \\nWord is: "+wordp+"\\nFinal score is: "+pkt);\r\n        window.setTimeout(gameOver, 600);\r\n    }\r\n}\r\nfunction gameOver(){\r\n    pkt=0;\r\n    hide();\r\n    showMenu();\r\n}\r\nfunction clearAlphabet(){\r\n    $("#alphabet span").removeClass("red green");\r\n}\r\nfunction check(val,styl){\r\n    if (wordp.indexOf(val) > -1){\r\n        $("#"+styl).addClass("green");\r\n        slowa(val);\r\n    }\r\n    else{\r\n        $("#"+styl).addClass("red");\r\n        szubienica();\r\n    }\r\n}\r\nfunction slowa(val){\r\n    n=wordp.length;\r\n    for(i=0; i<n; i++){\r\n        st=wordp.indexOf(val, i);\r\n        boxy[st]=val;}\r\n        hiden='''';\r\n    for(d=0; d<n; d++){\r\n        hiden+=boxy[d];\r\n        $("#pass").text(hiden);\r\n    }\r\n    win();\r\n}\r\nfunction wygrana(){\r\n        pkt++;\r\n    alert("Congratulations! \\nYour score is: "+pkt+"\\nLet''s continue!");\r\n    gra();\r\n    $("#back").toggle();\r\n}\r\n\r\nfunction win(){\r\n    if(hiden==wordp){\r\n    window.setTimeout(wygrana, 600);\r\n    }\r\n}\r\n</script>\r\n</html>\r\n\r\n\r\n[/raw]', 'HangMan', '', 'inherit', 'closed', 'closed', '', '715-revision-v1', '', '', '2017-10-30 22:44:16', '2017-10-30 22:44:16', '', 715, 'http://automatedquiz.com/quizstorm/2017/10/30/715-revision-v1/', 0, 'revision', '', 0),
