(754, 1, '2017-11-07 00:29:31', '2017-11-07 00:29:31', '[raw]\r\n\r\n\r\n<html>\r\n<style>\r\nbody {\r\n  margin: 0;\r\n  font-family: "Helvetica", "Verdana", "Roboto", sans-serif;\r\n}\r\n\r\nh1 {\r\n  margin: 0 0 15px;\r\n  padding: 15px;\r\n  color: #fff;\r\n  font-size: 40px;\r\n  font-weight: normal;\r\n  text-align: center;\r\n  background-color: #1f90ff;\r\n}\r\n\r\nform {\r\n  display: flex;\r\n  flex-flow: column nowrap;\r\n}\r\n\r\n.board {\r\n  position: relative;\r\n  width: 450px;\r\n  height: 450px;\r\n  margin: auto;\r\n}\r\n\r\n.field {\r\n  position: absolute;\r\n  top: 0;\r\n  left: 15px;\r\n  padding: 75px 0 0 0;\r\n  box-sizing: border-box;\r\n  width: 420px;\r\n  height: 435px;\r\n  overflow: hidden;\r\n  cursor: not-allowed;\r\n}\r\n\r\n.grid {\r\n  display: inline-flex;\r\n  flex-flow: column wrap;\r\n  position: relative;\r\n  min-width: 420px;\r\n  height: 360px;\r\n  counter-reset: player 1;\r\n}\r\n\r\n.column {\r\n  display: inline-flex;\r\n  flex-flow: column wrap;\r\n  height: 360px;\r\n}\r\n\r\n.front {\r\n  position: absolute;\r\n  top: 60px;\r\n  border: 15px solid #007fff;\r\n  border-radius: 4px;\r\n  box-sizing: border-box;\r\n  width: 450px;\r\n  height: 390px;\r\n  pointer-events: none;\r\n  background: radial-gradient(circle, transparent, transparent 18px, #007fff 20px, #007fff 23px, #1f90ff 23px, #1f90ff 36px, #007fff) center top/60px 60px;\r\n}\r\n\r\n/* Invisible inputs */\r\ninput {\r\n  display: none;\r\n  position: absolute;\r\n  top: -90px;\r\n  margin: 0;\r\n  width: 60px;\r\n  height: 450px;\r\n  cursor: pointer;\r\n  opacity: 0;\r\n}\r\ninput:indeterminate {\r\n  display: initial;\r\n}\r\n/* Inputs for red */\r\n.column > input:nth-of-type(2n) {\r\n  right: 360px;\r\n}\r\n.column > .column > input:nth-of-type(2n) {\r\n  right: 300px;\r\n}\r\n.column > .column > .column > input:nth-of-type(2n) {\r\n  right: 240px;\r\n}\r\n.column > .column > .column > .column > input:nth-of-type(2n) {\r\n  right: 180px;\r\n}\r\n.column